    QTest::newRow("1: small size matrix") << matrix_size_t{3u} << matrix_size_t{4u} << std::string{"Value1"} << matrix_size_t{3u} << matrix_size_t{5u} << matrix_opt_size_t{0u} << matrix_opt_size_t{0u};
